userStyleRules:
/* Change styling of pending (unloaded) tabs */
tab-item.discarded {
  opacity: 0.75;
}

/* Change styling of the favicon of pending (unloaded) tabs */
tab-item.discarded tab-favicon {
  display: none
}



/* Tab close button on hover */
.tab:not(:hover) .closebox {
  display: none;
}

/* Hide tab button */
.newtab-button-box {
  display: none;
}

#tabbar {
  bottom: 0 !important; /* Eliminate dead space on bottom */
}


/* regular width scrollbar */
#tabbar { scrollbar-width: auto; }

/* put scrollbar rightside */
:root.left #tabbar { direction: ltr; }

/** Colour background of tabs for different domains */
tab-item[data-current-uri*="redhat.com"] {
  background-color: #ee0000 !important;
/*  background-color: #cc0000 !important; */
}
tab-item[data-current-uri*="google.com"] {
  background-color: #4285F4 !important;
}
